# https://adventofcode.com/2015/day/1

def checkFloor(instructions):

    finalFloor = 0
    checkPoint = True
    pos = 0

    for floor in instructions:
        pos = pos + 1
        if floor == "(":
            finalFloor += 1
        elif floor == ")":
            finalFloor -= 1

        if checkPoint and finalFloor == -1:
            sendIndexGround(pos)
            checkPoint = False

    return finalFloor


def sendIndexGround(value):
    print(value)


